import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const users = [
  { firstName: "John", id: 1 },
  { firstName: "Emily", id: 2 },
  { firstName: "Michael", id: 3 },
  { firstName: "Sarah", id: 4 },
  { firstName: "David", id: 5 },
  { firstName: "Jessica", id: 6 },
  { firstName: "Daniel", id: 7 },
  { firstName: "Olivia", id: 8 },
  { firstName: "Matthew", id: 9 },
  { firstName: "Sophia", id: 10 }
]

const SearchBar = () => {
  const [searchText, setSearchText] = useState('');
  const [buttonColor, setButtonColor] = useState('green'); // Initial button color

  const searchBarStyle = {
    marginTop: '16%',
    display: 'flex',
    borderRadius: '20px',
    overflow: 'hidden',
    width: '600px',
  };

  const inputStyle = {
    flex: '1',
    border: 'none',
    borderRadius: '20px 0 0 20px',
    padding: '20px',
    fontSize: '18px',
    color: 'black',
  };

  const buttonStyle = {
    background: buttonColor,
    border: 'none',
    borderRadius: '0 20px 20px 0',
    padding: '15px 30px',
    cursor: 'pointer',
    fontSize: '18px',
    display: 'flex',
    alignItems: 'center',
    transition: 'background-color 0.3s ease-in-out', // Add a smooth transition effect
  };

  const clearButtonStyle = {
    cursor: 'pointer',
    padding: '15px',
    background: 'white',
    border: 'none',
    fontSize: '18px',
  };

  const [searchItem, setSearchItem] = useState('')
  const [filteredUsers, setFilteredUsers] = useState(users)

  const handleInputChange = (e) => {
    const searchTerm = e.target.value;
    setSearchItem(searchTerm)

    const filteredItems = users.filter((user) =>
      user.firstName.toLowerCase().includes(searchTerm.toLowerCase())
    );

    setFilteredUsers(filteredItems);
  }

  // const handleInputChange = (e) => {
  //   setSearchText(e.target.value);
  // };

  const handleClearClick = () => {
    setSearchText('');
  };

  const handleSearchClick = () => {
    setButtonColor(buttonColor === 'green' ? 'blue' : 'green');

    console.log('Search Text:', searchText);

    const newPageURL = 'https://example.com';
    window.open(newPageURL, '_blank');
  };


  return (
    <div style={searchBarStyle} className="search-bar">

      <input
        type="text" placeholder="üîç Search..." style={inputStyle}
        className="search-input"
        value={searchItem}
        onChange={handleInputChange}
      />

      {searchText && (
        <button style={clearButtonStyle} onClick={handleClearClick}>
          ‚úñÔ∏è
        </button>
      )
      }
      <ul className='names-possible'>
        {filteredUsers.map(user => <li key={user.id}>{user.firstName}</li>)}
      </ul>
      <button style={buttonStyle} className="search-button" onClick={handleSearchClick}>
        {/* Optional: You can include a magnifier icon here */}
        {/* <i className="fa fa-search"></i> */}
        Search
      </button>
    </div>

  );
}

export default SearchBar;
