import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
const SearchBar = () => {
  const [searchText, setSearchText] = useState('');
  const [buttonColor, setButtonColor] = useState('green'); // Initial button color

  const searchBarStyle = {
    marginTop: '16%',
    display: 'flex',
    borderRadius: '20px',
    overflow: 'hidden',
    width: '600px',
  };

  const inputStyle = {
    flex: '1',
    border: 'none',
    borderRadius: '20px 0 0 20px',
    padding: '20px',
    fontSize: '18px',
    color: 'black',
  };

  const buttonStyle = {
    background: buttonColor,
    border: 'none',
    borderRadius: '0 20px 20px 0',
    padding: '15px 30px',
    cursor: 'pointer',
    fontSize: '18px',
    display: 'flex',
    alignItems: 'center',
    transition: 'background-color 0.3s ease-in-out', // Add a smooth transition effect
  };

  const clearButtonStyle = {
    cursor: 'pointer',
    padding: '15px',
    background: 'white',
    border: 'none',
    fontSize: '18px',
  };

  const handleInputChange = (e) => {
    setSearchText(e.target.value);
  };

  const handleClearClick = () => {
    setSearchText('');
  };

  const YourComponent = () => {
    const navigate = useNavigate();

    const handleSearchClick = () => {
      // Toggle button color
      setButtonColor(buttonColor === 'green' ? 'blue' : 'green');

      // Log search text
      console.log('Search Text:', searchText);

      // Specify the route you want to navigate to
      const newPageRoute = '/new-page'; // Replace with your desired route

      // Use the navigate function to go to the new page
      navigate(newPageRoute);
    };


  return (
    <div style={searchBarStyle} className="search-bar">
      <input
        type="text" placeholder="üîç Search..." style={inputStyle}
        className="search-input"
        value={searchText}
        onChange={handleInputChange}
      />
      {searchText && (
        <button style={clearButtonStyle} onClick={handleClearClick}>
          ‚úñÔ∏è
        </button>
      )}
      <button style={buttonStyle} className="search-button" onClick={handleSearchClick}>
        {/* Optional: You can include a magnifier icon here */}
        {/* <i className="fa fa-search"></i> */}
        Search
      </button>
    </div>
  );
  }
}

export default SearchBar;
