import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const users = [
  { firstName: "John", id: 1 },
  { firstName: "Emily", id: 2 },
  { firstName: "Michael", id: 3 },
  { firstName: "Sarah", id: 4 },
  { firstName: "David", id: 5 },
  { firstName: "Jessica", id: 6 },
  { firstName: "Daniel", id: 7 },
  { firstName: "Olivia", id: 8 },
  { firstName: "Matthew", id: 9 },
  { firstName: "Sophia", id: 10 }
];

const SearchBar = () => {
  const [searchItem, setSearchItem] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [buttonColor, setButtonColor] = useState('green'); // Initial button color

  const searchBarStyle = {
    marginTop: '16%',
    display: 'flex',
    flexDirection: 'column',
    borderRadius: '20px',
    overflow: 'hidden',
    width: '600px',
  };

  const inputStyle = {
    borderRadius: '20px',
    padding: '15px',
    fontSize: '18px',
    color: 'black',
    marginBottom: '15px',
  };

  const dropdownStyle = {
    borderRadius: '20px',
    padding: '15px',
    fontSize: '18px',
    color: 'black',
  };

  const buttonStyle = {
    background: buttonColor,
    border: 'none',
    borderRadius: '0 0 20px 20px',
    padding: '15px 30px',
    cursor: 'pointer',
    fontSize: '18px',
    display: 'flex',
    alignItems: 'center',
    transition: 'background-color 0.3s ease-in-out', // Add a smooth transition effect
  };

  const handleInputChange = (e) => {
    setSearchItem(e.target.value);
    setSelectedUser(null); // Reset selected user when typing in the input
  };

  const handleDropdownChange = (e) => {
    const userId = parseInt(e.target.value, 10);
    const selectedUser = users.find(user => user.id === userId);
    setSelectedUser(selectedUser);
    setSearchItem(''); // Clear the input when selecting from the dropdown
  };

  const handleSearchClick = () => {
    setButtonColor(buttonColor === 'green' ? 'blue' : 'green');

    console.log('Search Text:', searchItem);
    console.log('Selected User:', selectedUser);

    // You can add additional functionality here for searching or navigation
  };

  return (
    <div style={searchBarStyle} className="search-bar">
      <input
        type="text"
        placeholder="ðŸ” Type and select..."
        style={inputStyle}
        className="search-input"
        value={searchItem}
        onChange={handleInputChange}
      />

      <select
        style={dropdownStyle}
        onChange={handleDropdownChange}
        value={selectedUser ? selectedUser.id : ''}
      >
        <option value="" disabled>Select a user</option>
        {users.map(user => (
          <option key={user.id} value={user.id}>{user.firstName}</option>
        ))}
      </select>

      <button style={buttonStyle} className="search-button" onClick={handleSearchClick}>
        Search
      </button>
    </div>
  );
}

export default SearchBar;
